//******************************************************************************
//  Function    : iSetISP_FE_ADAPTER_CFG
//  Description : Set the value of the member ISP_FE_ADAPTER_CFG
//******************************************************************************
static __inline HI_VOID isp_fe_sync_mode_write(S_ISPFE_REGS_TYPE *pstFeReg, HI_U32 uisp_fe_hsync_mode, HI_U32 uisp_fe_vsync_mode)
{
    if (!(uisp_fe_hsync_mode == 0) || !(uisp_fe_vsync_mode == 0))// Check the input value
    {
        return;
    }

    pstFeReg->ISP_FE_ADAPTER_CFG.u32 = (uisp_fe_vsync_mode << 1) + uisp_fe_hsync_mode;
}

